javascript:
// Add some dummy schedule
const schedule = document.getElementById('schedule');
const events = [
	['Event 1', '01/01/2020', '10:00 AM'],
	['Event 2', '02/02/2020', '11:00 AM']
];
events.forEach(event => {
	const row = document.createElement('tr');
	event.forEach(data => {
		const cell = document.createElement('td');
		cell.textContent = data;
		row.appendChild(cell);
	});
	schedule.appendChild(row);
});

// Simple chatbot greeting
const chatInput = document.getElementById('chat_input');
const chatOutput = document.getElementById('chat_output');
const sendBtn = document.getElementById('send_btn');
function updateChat(msg, sender){
	let message = document.createElement('p');
	message.textContent = `${sender}: ${msg}`;
	chatOutput.appendChild(message);
	chatOutput.scrollTop = chatOutput.scrollHeight;
}

sendBtn.addEventListener('click', ()=> {
	let userInput = chatInput.value;
	updateChat(userInput, 'You')
	updateChat('Hello!', 'AI')
	chatInput.value = '';
})

