let gamePaused = true;

// Add UI for adding an event
document.getElementById('add_event').addEventListener('click', () => {
    gamePaused = true;
    // code to show UI for adding event
});

// Add UI for updating the calendar
document.getElementById('update_calendar').addEventListener('click', () => {
    gamePaused = true;
    // code to show UI for updating the calendar
});

// Pause and resume game when update menu is opened/closed
document.getElementById('close_updates').addEventListener('click', () => {
    document.getElementById('updates').classList.add('hidden');
    gamePaused = false;
});

// AI chatbot - added basic conversation functionality
const responses = {
    'hello': 'Hi there!',
    'how are you': 'I am a bot, I do not have feelings!',
    'default': 'I do not understand what you are saying!',
};
sendBtn.addEventListener('click', () => {
    let userInput = chatInput.value.toLowerCase().trim();
    let response = responses[userInput] || responses['default'];
    updateChat(userInput, 'You');
    updateChat(response, 'AI');
    chatInput.value = '';
});
