css:
body {
  background-color: #00171F;
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  margin: 0;
  overflow: hidden;
  font-family: 'Press Start 2P', cursive;
}

#game {
  position: relative;
  height: 500px;
  width: 500px;
  background: #003459;
}

.entity {
  position: absolute;
  border-radius: 50%;
}

#player {
  height: 25px;
  width: 25px;
  background-color: #007EA7;
  box-shadow: 0px 0px 10px #007EA7;
}

.enemy {
  height: 25px;
  width: 25px;
  background-color: #00A8E8;
}

#goal {
  height: 50px;
  width: 50px;
  background-color: #F2EFE9;
}

.powerup {
  height: 20px;
  width: 20px;
  background-color: #F2A541;
  display: none;
}

#scoreBoard, #timer, #lifeBoard, #message, #updateMenu {
  position: fixed;
  top: 10px;
  color: #F2EFE9;
  text-shadow: 2px 2px #1C2541;
  font-size: 24px;
}

#scoreBoard {
  left: 15px;
}

#lifeBoard {
  left: 180px;
}

#timer {
  left: 285px;
}

#message, #updateMenu {
  display: none;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(255,68,68,0.9);
  color: white;
  padding: 20px;
  border-radius: 15px;
  font-size: 24px;
}

#message {
  background-color: rgba(255,68,68,0.9);
}

#updateMenu {
  background-color: rgba(255,255,255,0.9);
  color: black;
}

#restart, #pause, #closeUpdate {
  padding: 10px 20px;
  font-size: 18px;
  border: none;
  background-color: #F22613;
  color: white;
  cursor: pointer;
  border-radius: 5px;
}

#restart {
  position: fixed;
  right: 15px;
  top: 10px;
}

#pause {
  position: fixed;
  right: 15px;
  top: 50px;
}

#closeUpdate {
  display: block;
  margin: auto;
}

javascript:
var playerPosition = { top: 0, left: 0 };
var enemyPositions = [
  { top: 100, left: 100 },
  { top: 200, left: 200 },
  { top: 300, left: 300 },
];
var goalPosition = { top: 475, left: 475 };
var powerupPositions = [
  { top: 0, left: 0 },
  { top: 0, left: 0 },
  { top: 0, left: 0 },
];
var speed = 20;
var enemySpeed = 0.5;
var score = 0;
var lives = 3;
var gamePaused = false;
var startTime = Date.now();
var timerInterval;
var messages = [
  'Game Paused',
  'Power Up acquired!! Speed increased',
  'Power Up acquired!! Enemies slowed',
  'Power Up acquired!! Lives increased',
];
var updateList = [
  'New: Visual Enhancements',
  'New: Dynamic Powerups',
  'Fix: Improved Collision Detection',
  'Fix: Boundary Check for Player',
  'Gameplay: Faster Player Movement',
  'Gameplay: Slower Enemy Movement',
];

function updatePositions() {
  var entities = document.getElementsByClassName('entity');
  for (var i = 0; i < entities.length; i++) {
    var entity = entities[i];
    if (entity.id === 'player') {
      entity.style.top = playerPosition.top + 'px';
      entity.style.left = playerPosition.left + 'px';
    } else if (entity.classList.contains('enemy')) {
      var enemyIndex = Number(entity.id[entity.id.length - 1]) - 1;
      entity.style.top = enemyPositions[enemyIndex].top + 'px';
      entity.style.left = enemyPositions[enemyIndex].left + 'px';
    } else if (entity.classList.contains('powerup')) {
      var powerupIndex = Number(entity.id[entity.id.length - 1]) - 1;
      entity.style.top = powerupPositions[powerupIndex].top + 'px';
      entity.style.left = powerupPositions[powerupIndex].left + 'px';
    }
  }
}

document.addEventListener('keydown', function (e) {
  var player = document.getElementById('player');
  if (!gamePaused) {
    switch (e.key) {
      case 'w':
        playerPosition.top = Math.max(
          0,
          playerPosition.top - speed
        );
        break;
      case 'a':
        playerPosition.left = Math.max(
          0,
          playerPosition.left - speed
        );
        break;
      case 's':
        playerPosition.top = Math.min(
          document.getElementById('game').offsetHeight -
            player.offsetHeight,
          playerPosition.top + speed
        );
        break;
      case 'd':
        playerPosition.left = Math.min(
          document.getElementById('game').offsetWidth -
            player.offsetWidth,
          playerPosition.left + speed
        );
        break;
    }
  }
  updatePositions();
  checkCollisions();
});

function enemyMovement(enemyPosition) {
  var game = document.getElementById('game');
  var playerRect = document.getElementById('player').getBoundingClientRect();
  var gameRect = game.getBoundingClientRect();

  if (
    enemyPosition.top < playerPosition.top &&
    enemyPosition.top + enemySpeed < gameRect.height
  )
    enemyPosition.top += enemySpeed;
  else if (
    enemyPosition.top > playerPosition.top &&
    enemyPosition.top - enemySpeed > 0
  )
    enemyPosition.top -= enemySpeed;

  if (
    enemyPosition.left < playerPosition.left &&
    enemyPosition.left + enemySpeed < gameRect.width
  )
    enemyPosition.left += enemySpeed;
  else if (
    enemyPosition.left > playerPosition.left &&
    enemyPosition.left - enemySpeed > 0
  )
    enemyPosition.left -= enemySpeed;
}

function checkCollisions() {
  var player = document.getElementById('player');
  var playerRect = player.getBoundingClientRect();
  var goal = document.getElementById('goal');
  var goalRect = goal.getBoundingClientRect();

  if (overlapCheck(playerRect, goalRect)) {
    score++;
    document.getElementById('scoreBoard').innerText =
      'Score: ' + score;
    resetPositions();
  }

  var enemies = document.getElementsByClassName('enemy');
  for (var i = 0; i < enemies.length; i++) {
    var enemyRect = enemies[i].getBoundingClientRect();
    if (overlapCheck(playerRect, enemyRect)) {
      lives--;
      document.getElementById('lifeBoard').innerText =
        'Lives: ' + lives;
      if (lives <= 0) {
        gameEnd();
      } else {
        resetPositions();
        spawnPowerups();
      }
    }
  }

  var powerups = document.getElementsByClassName('powerup');
  for (var i = 0; i < powerups.length; i++) {
    var powerupRect = powerups[i].getBoundingClientRect();
    if (overlapCheck(playerRect, powerupRect)) {
      powerups[i].style.display = 'none';
      activatePowerup(i);
    }
  }
}

function gameEnd() {
  gamePaused = true;
  document.getElementById('message').innerText = 'Game Over';
  document.getElementById('message').style.display = 'block';
}

document.getElementById('restart').addEventListener('click', function () {
  location.reload();
});

document.getElementById('pause').addEventListener('click', function () {
  gamePaused = !gamePaused;
  if (gamePaused) {
    clearInterval(timerInterval);
    showMessage(0);
  } else {
    timerInterval = setInterval(updateGame, 10);
    document.getElementById('message').style.display =
      'none';
  }
});

function spawnPowerups() {
  var powerups = document.getElementsByClassName('powerup');
  for (var i = 0; i < powerups.length; i++) {
    var randomPos = getRandomPosition();
    powerupPositions[i] = randomPos;
    powerups[i].style.top = randomPos.top + 'px';
    powerups[i].style.left = randomPos.left + 'px';
    powerups[i].style.display = 'block';
  }
}

function getRandomPosition() {
  var game = document.getElementById('game');
  var x = Math.floor(Math.random() * (game.offsetWidth - 20));
  var y = Math.floor(Math.random() * (game.offsetHeight - 20));
  return { top: y, left: x };
}

function overlapCheck(rect1, rect2) {
  return (
    !(rect1.right < rect2.left ||
      rect1.left > rect2.right ||
      rect1.bottom < rect2.top ||
      rect1.top > rect2.bottom)
  );
}

function activatePowerup(powerupId) {
  switch (powerupId) {
    case 0:
      speed += 5;
      showMessage(1);
      break;
    case 1:
      enemySpeed -= 0.2;
      showMessage(2);
      break;
    case 2:
      lives++;
      document.getElementById('lifeBoard').innerText =
        'Lives: ' + lives;
      showMessage(3);
      break;
  }
}

function showMessage(id) {
  document.getElementById('message').innerText = messages[id];
  document.getElementById('message').style.display = 'block';
  setTimeout(function () {
    document.getElementById('message').style.display = 'none';
  }, 2000);
}

function resetPositions() {
  playerPosition = { top: 0, left: 0 };
  enemyPositions = [
    { top: 100, left: 100 },
    { top: 200, left: 200 },
    { top: 300, left: 300 },
  ];
  updatePositions();
}

function updateGame() {
  if (!gamePaused) {
    enemyPositions.forEach(enemyMovement);
    updatePositions();
    checkCollisions();
  }
}

timerInterval = setInterval(updateGame, 10);

function showUpdateMenu() {
  var updateMenu = document.getElementById('updateMenu');
  var updateListElem = document.getElementById('updateList');

  for (var i = 0; i < updateList.length; i++) {
    var newItem = document.createElement('li');
    newItem.innerText = updateList[i];
    updateListElem.appendChild(newItem);
  }
  updateMenu.style.display = 'block';
}

document.getElementById('closeUpdate').addEventListener('click', function () {
  document.getElementById('updateMenu').style.display = 'none';
});

