var playerPosition = { top: 0, left: 0 };
var goalPosition = { top: 475, left: 475 };
var speed = 20;
var score = 0;
var level = 1;
var lives = 4;
var displayScore = document.getElementById("scoreBoard");
var displayLives = document.getElementById("lifeBoard");
var displayLevel = document.getElementById("levelDisplay");
var gamePaused = false;

var updateList = [
  "New: Visual Enhancements",
  "New: Player's size increases by consuming food",
  "Fix: Improved Collision Detection",
  "Fix: Boundary Check for Player",
  "Gameplay: Faster Player Movement",
  "New: Pause and Resume Game",
  "New: Timer to track time spent on game",
  "New: Restart Game Button",
];

showUpdateMenu();

function updatePositions() {
  var player = document.getElementById("player");
  player.style.top = playerPosition.top + "px";
  player.style.left = playerPosition.left + "px";

  var goal = document.getElementById("goal");
  goal.style.top = goalPosition.top + "px";
  goal.style.left = goalPosition.left + "px";
}

document.addEventListener("keydown", function (e) {
  var player = document.getElementById("player");
  var gameArea = document.getElementById("game");
  if (!gamePaused) {
    switch (e.key) {
      case "w":
        if (playerPosition.top - speed < 0) return;
        playerPosition.top -= speed;
        break;
      case "a":
        if (playerPosition.left - speed < 0) return;
        playerPosition.left -= speed;
        break;
      case "s":
        if (
          playerPosition.top + player.offsetHeight + speed >
          gameArea.offsetHeight
        )
          return;
        playerPosition.top += speed;
        break;
      case "d":
        if (
          playerPosition.left + player.offsetWidth + speed >
          gameArea.offsetWidth
        )
          return;
        playerPosition.left += speed;
        break;
    }
  }
  updatePositions();
  checkCollisions();
});

function checkCollisions() {
  var player = document.getElementById("player");
  var playerRect = player.getBoundingClientRect();
  var goal = document.getElementById("goal");
  var goalRect = goal.getBoundingClientRect();

  if (overlapCheck(playerRect, goalRect)) {
    score++;
    displayScore.innerText = "Score: " + score;
    if (score % 50 == 0) {
      speed += 5;
    }
    if (score % 100 == 0) {
      levelUp();
    }
    resetPositions();
  }
}

function overlapCheck(rect1, rect2) {
  return !(
    rect1.right < rect2.left ||
    rect1.left > rect2.right ||
    rect1.bottom < rect2.top ||
    rect1.top > rect2.bottom
  );
}

function resetPositions() {
  playerPosition = { top: 0, left: 0 };
  goalPosition = getRandomPosition();
  updatePositions();
}

function getRandomPosition() {
  var gameArea = document.getElementById("game");
  var x = Math.floor(Math.random() * (gameArea.offsetWidth - 20));
  var y = Math.floor(Math.random() * (gameArea.offsetHeight - 20));
  return { top: y, left: x };
}

function levelUp() {
  level++;
  speed += 5;
  displayLevel.innerText = "Level: " + level;
}

document.getElementById("restart").addEventListener("click", function () {
  location.reload();
});

document.getElementById("pause").addEventListener("click", function () {
  gamePaused = !gamePaused;
});

function updateGame() {
  if (!gamePaused) {
    checkCollisions();
  }
}

function showUpdateMenu() {
  gamePaused = true;
  var updateMenu = document.getElementById("updateMenu");
  var updateListElem = document.getElementById("updateList");
  updateList.forEach(function (item) {
    var li = document.createElement("li");
    li.innerText = item;
    updateListElem.appendChild(li);
  });
  updateMenu.style.display = "block";
}

document.getElementById("closeUpdate").addEventListener("click", function () {
  document.getElementById("updateMenu").style.display = "none";
  gamePaused = false;
});

setInterval(updateGame, 10);
