      break;
  }
  updatePositions();
  checkWin();
});

setInterval(function() {
  if (enemy1Position.top < playerPosition.top) {
    enemy1Position.top += enemySpeed;
  }
  if(enemy1Position.top > playerPosition.top){
    enemy1Position.top -= enemySpeed;
  }
  if(enemy1Position.left < playerPosition.left){
    enemy1Position.left += enemySpeed;
  }
  if(enemy1Position.left > playerPosition.left){
    enemy1Position.left -= enemySpeed;
  }
  if (enemy2Position.top < playerPosition.top) {
    enemy2Position.top += enemySpeed;
  }
  if(enemy2Position.top > playerPosition.top){
    enemy2Position.top -= enemySpeed;
  }
  if(enemy2Position.left < playerPosition.left){
    enemy2Position.left += enemySpeed;
  }
  if(enemy2Position.left > playerPosition.left){
    enemy2Position.left -= enemySpeed;
  }
  updatePositions();
  checkWin();
}, 10);

function checkWin() {
  var playerRect = document.getElementById('player').getBoundingClientRect();
  var enemy1Rect = document.getElementById('enemy1').getBoundingClientRect();
  var enemy2Rect = document.getElementById('enemy2').getBoundingClientRect();
  var goalRect = document.getElementById('goal').getBoundingClientRect();

  var playerEnemy1Overlap = !(playerRect.right < enemy1Rect.left || 
