javascript:
var playerPosition = { top: 0, left: 0 };
var enemy1Position = { top: 200, left: 200 };
var enemy2Position = { top: 300, left: 300 };
var goalPosition = { top: 475, left: 475 };
var powerup1Position = { top: Math.random()*475, left: Math.random()*475 };
var speed = 20;
var enemySpeed = 1;
var score = 0;
var lives = 3;

function updatePositions() {
    document.getElementById('player').style.top = playerPosition.top + "px";
    document.getElementById('player').style.left = playerPosition.left + "px";
    document.getElementById('enemy1').style.top = enemy1Position.top + "px";
    document.getElementById('enemy1').style.left = enemy1Position.left + "px";
    document.getElementById('enemy2').style.top = enemy2Position.top + "px";
    document.getElementById('enemy2').style.left = enemy2Position.left + "px";
    document.getElementById('scoreBoard').innerText = 'Score: ' + score + ' pts';
    document.getElementById('lifeBoard').innerText = 'Lives: ' + lives;
    document.getElementById('powerup1').style.top = powerup1Position.top + "px";
    document.getElementById('powerup1').style.left = powerup1Position.left + "px";
}

document.addEventListener('keydown', function(e) {
  switch (e.key) {
    case "w":
      playerPosition.top = Math.max(0, playerPosition.top - speed);
      break;
    case "a":
      playerPosition.left = Math.max(0, playerPosition.left - speed);
      break;
    case "s":
      playerPosition.top = Math.min(document.getElementById('game').offsetHeight - document.getElementById('player').offsetHeight, playerPosition.top + speed);
      break;
    case "d":
      playerPosition.left = Math.min(document.getElementById('game').offsetWidth - document.getElementById('player').offsetWidth, playerPosition.left + speed);
      break;
  }
  updatePositions();
  checkWin();
});

setInterval(function() {
  if (enemy1Position.top < playerPosition.top) {
    enemy1Position.top += enemySpeed;
  }
  if(enemy1Position.top > playerPosition.top){
    enemy1Position.top -= enemySpeed;
  }
  if(enemy1Position.left < playerPosition.left){
    enemy1Position.left += enemySpeed;
  }
  if(enemy1Position.left > playerPosition.left){
    enemy1Position.left -= enemySpeed;
  }
  if (enemy2Position.top < playerPosition.top) {
    enemy2Position.top += enemySpeed;
  }
  if(enemy2Position.top > playerPosition.top){
    enemy2Position.top -= enemySpeed;
  }
  if(enemy2Position.left < playerPosition.left){
    enemy2Position.left += enemySpeed;
  }
  if(enemy2Position.left > playerPosition.left){
    enemy2Position.left -= enemySpeed;
  }
  updatePositions();
  checkWin();
}, 10);

function checkWin() {
  var playerRect = document.getElementById('player').getBoundingClientRect();
  var enemy1Rect = document.getElementById('enemy1').getBoundingClientRect();
  var enemy2Rect = document.getElementById('enemy2').getBoundingClientRect();
  var goalRect = document.getElementById('goal').getBoundingClientRect();
  var powerup1Rect = document.getElementById('powerup1').getBoundingClientRect();

  var playerEnemy1Overlap = !(playerRect.right < enemy1Rect.left || 
                  playerRect.left > enemy1Rect.right || 
                  playerRect.bottom < enemy1Rect.top || 
                  playerRect.top > enemy1Rect.bottom);

  var playerEnemy2Overlap = !(playerRect.right < enemy2Rect.left || 
                  playerRect.left > enemy2Rect.right || 
                  playerRect.bottom < enemy2Rect.top || 
                  playerRect.top > enemy2Rect.bottom);

  var playerGoalOverlap = !(playerRect.right < goalRect.left || 
                  playerRect.left > goalRect.right || 
                  playerRect.bottom < goalRect.top || 
                  playerRect.top > goalRect.bottom);

  var playerPowerup1Overlap = !(playerRect.right < powerup1Rect.left || 
                  playerRect.left > powerup1Rect.right || 
                  playerRect.bottom < powerup1Rect.top || 
                  playerRect.top > powerup1Rect.bottom);

  if (playerEnemy1Overlap || playerEnemy2Overlap) {
    lives -= 1;
    playerPosition = { top: 0, left: 0 };
    enemy1Position = { top: Math.random()*475, left: Math.random()*475 };
    enemy2Position = { top: Math.random()*475, left: Math.random()*475 };
    document.getElementById('powerup1').style.display = "none";
    if(lives==0){
      location.reload();
    }
  } else if (playerGoalOverlap) {
    score += 10;
    playerPosition = { top: 0, left: 0 };
    enemy1Position = { top: Math.random()*475, left: Math.random()*475 };
    enemy2Position = { top: Math.random()*475, left: Math.random()*475 };
    document.getElementById('powerup1').style.display = "none";
    updatePositions();
  } else if (playerPowerup1Overlap && document.getElementById('powerup1').style.display != "none") {
    score += 5;
    document.getElementById('powerup1').style.display = "none";
    updatePositions();
  }
}

document.getElementById('restart').addEventListener('click', function() {
  location.reload();
});

setInterval(function() {
  if(Math.random() < 0.1){
    document.getElementById('powerup1').style.display = "block";
    powerup1Position = { top: Math.random()*475, left: Math.random()*475 };
    updatePositions();
  }
}, 10000);

