var enemy2Position = { top: 310, left: 290 };
var enemy3Position = { top: 400, left: 400 };
var goalPosition = { top: 475, left: 475 };
var powerup1Position = { top: Math.random() * 475, left: Math.random() * 475 };
var powerup2Position = { top: Math.random() * 475, left: Math.random() * 475 };
var speed = 20;
var enemySpeed = 1;
var score = 0;
var lives = 3;

function updatePositions() {
    document.getElementById('player').style.top = playerPosition.top + "px";
    document.getElementById('player').style.left = playerPosition.left + "px";
    document.getElementById('enemy1').style.top = enemy1Position.top + "px";
    document.getElementById('enemy1').style.left = enemy1Position.left + "px";
    document.getElementById('enemy2').style.top = enemy2Position.top + "px";
    document.getElementById('enemy2').style.left = enemy2Position.left + "px";
    document.getElementById('enemy3').style.top = enemy3Position.top + "px";
    document.getElementById('enemy3').style.left = enemy3Position.left + "px";
    document.getElementById('scoreBoard').innerText = 'Score: ' + score + ' pts';
    document.getElementById('lifeBoard').innerText = 'Lives: ' + lives;
    document.getElementById('powerup1').style.top = powerup1Position.top + "px";
    document.getElementById('powerup1').style.left = powerup1Position.left + "px";
    document.getElementById('powerup2').style.top = powerup2Position.top + "px";
    document.getElementById('powerup2').style.left = powerup2Position.left + "px";
}

document.addEventListener('keydown', function(e) {
    switch (e.key) {
        case "w":
            playerPosition.top = Math.max(0, playerPosition.top - speed);
            break;
        case "a":
            playerPosition.left = Math.max(0, playerPosition.left - speed);
            break;
        case "s":
            playerPosition.top = Math.min(document.getElementById('game').offsetHeight - document.getElementById('player').offsetHeight, playerPosition.top + speed);
            break;
        case "d":
            playerPosition.left = Math.min(document.getElementById('game').offsetWidth - document.getElementById('player').offsetWidth, playerPosition.left + speed);
            break;
    }

    updatePositions();
    checkWin();
});

setInterval(function() {
    enemyMovement('enemy1', enemy1Position);
    enemyMovement('enemy2', enemy2Position);
    enemyMovement('enemy3', enemy3Position);

    updatePositions();
    checkWin();
}, 10);

function enemyMovement(enemyID, enemyPosition) {
    var playerRect = document.getElementById('player').getBoundingClientRect();
    var enemyRect = document.getElementById(enemyID).getBoundingClientRect();

    if (enemyRect.top < playerRect.top) {
        enemyPosition.top += enemySpeed;
    }
    if (enemyRect.top > playerRect.top) {
        enemyPosition.top -= enemySpeed;
    }
    if (enemyRect.left < playerRect.left) {
        enemyPosition.left += enemySpeed;
    }
    if (enemyRect.left > playerRect.left) {
        enemyPosition.left -= enemySpeed;
    }
}

function checkWin() {
    var rects = {
        player: document.getElementById('player').getBoundingClientRect(),
        enemy1: document.getElementById('enemy1').getBoundingClientRect(),
        enemy2: document.getElementById('enemy2').getBoundingClientRect(),
        enemy3: document.getElementById('enemy3').getBoundingClientRect(),
        goal: document.getElementById('goal').getBoundingClientRect(),
        powerup1: document.getElementById('powerup1').getBoundingClientRect(),
        powerup2: document.getElementById('powerup2').getBoundingClientRect(),
    };

    var overlaps = {
        playerEnemy1: overlapCheck(rects.player, rects.enemy1),
        playerEnemy2: overlapCheck(rects.player, rects.enemy2),
        playerEnemy3: overlapCheck(rects.player, rects.enemy3),
        playerGoal: overlapCheck(rects.player, rects.goal),
        playerPowerup1: overlapCheck(rects.player, rects.powerup1),
        playerPowerup2: overlapCheck(rects.player, rects.powerup2)
    }

    if (overlaps.playerEnemy1 || overlaps.playerEnemy2 || overlaps.playerEnemy3) {
        loseLife();
    } else if (overlaps.playerGoal) {
        score += 15;
        goalReached();
    } else if (overlaps.playerPowerup1 && document.getElementById('powerup1').style.display != "none") {
        score += 5;
        document.getElementById('powerup1').style.display = "none";
        updatePositions();
    } else if (overlaps.playerPowerup2 && document.getElementById('powerup2').style.display != "none") {
        lives += 1;
        document.getElementById('powerup2').style.display = "none";
        updatePositions();
    }
}

