javascript:
var playerPosition = { top: 0, left: 0 };
var enemyPositions = [{ top: 100, left: 100 },
                      { top: 200, left: 200 },
                      { top: 300, left: 300 }];
var goalPosition = { top: 475, left: 475 };
var powerupPositions = [{ top: 0, left: 0 },
                        { top: 0, left: 0 },
                        { top: 0, left: 0 }];
var speed = 20;
var enemySpeed = 0.5;
var score = 0;
var lives = 3;
var gamePaused = false;
var startTime = Date.now();
var timerInterval;
var messages = ['Game Paused', 'Power Up acquired!! Speed increased', 'Power Up acquired!! Enemies slowed', 'Power Up acquired!! Lives increased'];
spawnPowerups();

function updatePositions() {
    var player = document.getElementById('player');
    player.style.top = playerPosition.top + 'px';
    player.style.left = playerPosition.left + 'px';

    var enemies = document.getElementsByClassName('enemy');
    for (var i = 0; i < enemies.length; i++) {
      enemies[i].style.top = enemyPositions[i].top + 'px';
      enemies[i].style.left = enemyPositions[i].left + 'px';
    }

    var powerups = document.getElementsByClassName('powerup');
    for (var i = 0; i < powerups.length; i++) {
      powerups[i].style.top = powerupPositions[i].top + 'px';
      powerups[i].style.left = powerupPositions[i].left + 'px';
    }
}

document.addEventListener('keydown', function(e) {
    var player = document.getElementById('player');
    if (!gamePaused) {
        switch (e.key) {
            case "w":
                playerPosition.top = Math.max(0, playerPosition.top - speed);
                break;
            case "a":
                playerPosition.left = Math.max(0, playerPosition.left - speed);
                break;
            case "s":
                playerPosition.top = Math.min(document.getElementById('game').offsetHeight - player.offsetHeight, playerPosition.top + speed);
                break;
            case "d":
                playerPosition.left = Math.min(document.getElementById('game').offsetWidth - player.offsetWidth, playerPosition.left + speed);
                break;
        }
    }
    updatePositions();
    checkCollisions();
});

function enemyMovement(enemyPosition) {
    var game = document.getElementById('game');
    var playerRect = document.getElementById('player').getBoundingClientRect();
    var gameRect = game.getBoundingClientRect();

    if (enemyPosition.top < playerPosition.top && enemyPosition.top + enemySpeed < gameRect.height)
        enemyPosition.top += enemySpeed
    else if (enemyPosition.top > playerPosition.top && enemyPosition.top - enemySpeed > 0)
        enemyPosition.top -= enemySpeed

    if (enemyPosition.left < playerPosition.left && enemyPosition.left + enemySpeed < gameRect.width)
        enemyPosition.left += enemySpeed
    else if (enemyPosition.left > playerPosition.left && enemyPosition.left - enemySpeed > 0)
        enemyPosition.left -= enemySpeed
}

function checkCollisions() {
    var player = document.getElementById('player');
    var playerRect = player.getBoundingClientRect();
    var goal = document.getElementById('goal');
    var goalRect = goal.getBoundingClientRect();

    if (overlapCheck(playerRect, goalRect)) {
        score++;
        document.getElementById('scoreBoard').innerText = "Score: " + score;
        resetPositions();
    }

    var enemies = document.getElementsByClassName('enemy');
    for (var i = 0; i < enemies.length; i++) {
        var enemyRect = enemies[i].getBoundingClientRect();
        if (overlapCheck(playerRect, enemyRect)) {
            lives--;
            document.getElementById('lifeBoard').innerText = "Lives: " + lives;
            resetPositions();
            if(lives <= 0){
                gameEnd();
            }
        }
    }

    var powerups = document.getElementsByClassName('powerup');
    for (var i = 0; i < powerups.length; i++) {
        var powerupRect = powerups[i].getBoundingClientRect();
        if (overlapCheck(playerRect, powerupRect)) {
            powerups[i].style.display = 'none';
            activatePowerup(i);
        }
    }
}

function gameEnd() {
    gamePaused = true;
    document.getElementById('message').innerText = 'Game Over';
    document.getElementById('message').style.display = 'block';
}

document.getElementById('restart').addEventListener('click', function() {
    location.reload();
});

document.getElementById('pause').addEventListener('click', function() {
    gamePaused = !gamePaused;
    if(gamePaused){
        clearInterval(timerInterval);
        showMessage(0);
    } else{
        timerInterval = setInterval(updateGame, 10);
        document.getElementById('message').style.display = 'none';
    }
});

function spawnPowerups() {
    var powerups = document.getElementsByClassName('powerup');
    for (var i = 0; i < powerups.length; i++) {
        var randomPos = getRandomPosition();
        powerupPositions[i] = randomPos;
        powerups[i].style.top = randomPos.top + 'px';
        powerups[i].style.left = randomPos.left + 'px';
        powerups[i].style.display = 'block';
    }
}

function getRandomPosition() {
    var game = document.getElementById('game');
    var x = Math.floor(Math.random() * (game.offsetWidth-20));
    var y = Math.floor(Math.random() * (game.offsetHeight-20));
    return { top: y, left: x };
}

function overlapCheck(rect1, rect2) {
    return !(rect1.right < rect2.left || 
             rect1.left > rect2.right || 
             rect1.bottom < rect2.top || 
             rect1.top > rect2.bottom)
}

function activatePowerup(powerupId) {
    switch(powerupId) {
        case 0:
            speed += 5;
            showMessage(1);
            break;
        case 1:
            enemySpeed -= 0.2;
            showMessage(2);
            break;
        case 2:
            lives++;
            document.getElementById('lifeBoard').innerText = "Lives: " + lives;
            showMessage(3);
            break;
    }
}

function showMessage(id) {
    document.getElementById('message').innerText = messages[id];
    document.getElementById('message').style.display = 'block';
    setTimeout(function() {
        document.getElementById('message').style.display = 'none';
    }, 1000);
}

function resetPositions() {
    playerPosition = { top: 0, left: 0 };
    enemyPositions = [{ top: 100, left: 100 },
                    { top: 200, left: 200 },
                    { top: 300, left: 300 }];
    updatePositions();
}

function updateGame() {
    if (!gamePaused) {
        enemyPositions.forEach(enemyMovement);
        updatePositions();
        checkCollisions();
    }
}

timerInterval = setInterval(updateGame, 10);

