    if (overlapCheck(playerRect, enemyRect)) {
      lives--;
      displayLives.innerText = 'Lives: ' + lives;
      if (lives <= 0) {
        gameEnd();
      } else {
        resetPositions();
        spawnPowerups();
      }
    }
  }

  var powerups = document.getElementsByClassName('powerup');
  for (var i = 0; i < powerups.length; i++) {
    var powerupRect = powerups[i].getBoundingClientRect();
    if (overlapCheck(playerRect, powerupRect)) {
      powerups[i].style.display = 'none';
      activatePowerup(i);
    }
  }

  if(overlapCheck(playerRect, specialRect)) {
    special.style.display = 'none';
    powerupCounter++;
    displayPowerupCounter.innerText = 'Powerups: ' + powerupCounter;
    speed += 10;
    enemySpeed -= 0.5;
    showMessage(5);
  }
