  if(Math.random() > 0.5) {
    var specialPos = getRandomPosition();
    specialPowerupPosition = specialPos;
    special.style.top = specialPos.top + 'px';
    special.style.left = specialPos.left + 'px';
    special.style.display = 'block';
  }
}

