  'New: Visual Enhancements',
  'New: Dynamic Powerups',
  'Fix: Improved Collision Detection',
  'Fix: Boundary Check for Player',
  'Gameplay: Faster Player Movement',
  'Gameplay: Slower Enemy Movement',
  'Gameplay: Player size decreases every 50pts',
  'New: Timer for games',
  'New: More enemies',
];

showUpdateMenu();
