function updatePositions() {
  var entities = document.getElementsByClassName('entity');
  for (var i = 0; i < entities.length; i++) {
    var entity = entities[i];
    if (entity.id === 'player') {
      entity.style.top = playerPosition.top + 'px';
      entity.style.left = playerPosition.left + 'px';
    } else if (entity.classList.contains('enemy')) {
      var enemyIndex = Number(entity.id[entity.id.length - 1]) - 1;
      entity.style.top = enemyPositions[enemyIndex].top + 'px';
      entity.style.left = enemyPositions[enemyIndex].left + 'px';
    } else if (entity.classList.contains('powerup')) {
      var powerupIndex = Number(entity.id[entity.id.length - 1]) - 1;
      entity.style.top = powerupPositions[powerupIndex].top + 'px';
      entity.style.left = powerupPositions[powerupIndex].left + 'px';
    } else if (entity.id === 'special') {
      entity.style.top = specialPowerupPosition.top + 'px';
      entity.style.left = specialPowerupPosition.left + 'px';
    }
  }
}
//part 3
document.addEventListener('keydown', function (e) {
  var player = document.getElementById('player');
  if (!gamePaused) {
    switch (e.key) {
      case 'w':
        playerPosition.top = Math.max(
          0,
          playerPosition.top - speed
        );
        break;
      case 'a':
        playerPosition.left = Math.max(
          0,
          playerPosition.left - speed
        );
        break;
      case 's':
        playerPosition.top = Math.min(
          document.getElementById('game').offsetHeight -
            player.offsetHeight,
          playerPosition.top + speed
        );
        break;
      case 'd':
        playerPosition.left = Math.min(
          document.getElementById('game').offsetWidth -
            player.offsetWidth,
          playerPosition.left + speed
        );
        break;
    }
  }
  updatePositions();
  checkCollisions();
});

function enemyMovement(enemyPosition) {
  var game = document.getElementById('game');

  if (
    enemyPosition.top < playerPosition.top &&
    enemyPosition.top + enemySpeed < game.offsetWidth
  )
    enemyPosition.top += enemySpeed;
  else if (
    enemyPosition.top > playerPosition.top &&
    enemyPosition.top - enemySpeed > 0
  )
    enemyPosition.top -= enemySpeed;

  if (
    enemyPosition.left < playerPosition.left &&
    enemyPosition.left + enemySpeed < game.offsetHeight
  )
    enemyPosition.left += enemySpeed;
  else if (
    enemyPosition.left > playerPosition.left &&
    enemyPosition.left - enemySpeed > 0
  )
    enemyPosition.left -= enemySpeed;
}
