  var specialRect = special.getBoundingClientRect();

  if (overlapCheck(playerRect, goalRect)) {
    score++;
    displayScore.innerText = 'Score: ' + score;
    if(score % 50 == 0){
        reducePlayerSize();
        showMessage(4);
    }
    resetPositions();
    if(score % 30 == 0){
      spawnEnemy();
      showMessage(6);
    }
