  var sec = totalSec % 60;
  var timer = document.getElementById('timer');
  timer.innerText = 'Timer: ' + min + 'm' + ' ' + sec + 's';
  setTimeout(displayTime, 1000);
}

function spawnEnemy() {
  var enemyPos = getRandomPosition();
  enemyPositions.push({top:enemyPos.top, left:enemyPos.left})
  var newEnemy = document.createElement('div');
  newEnemy.classList.add("enemy");
  newEnemy.classList.add("entity");
  newEnemy.setAttribute("id", "enemy" + enemyPositions.length);
