    'Fix: Improved Collision Detection',
    'Fix: Boundary Check for Player',
    'Gameplay: Increased Player Movement',
    'Gameplay: Decreased Enemy Movement',
    'New: Timer for games',
    'New: More enemies',
    'New: More power-ups',
    'New Gameplay: Speed increases over time'
  ];
  var updateMenu = document.getElementById('updateMenu');
  var updateListElem = document.getElementById('updateList');
  for (var i = 0; i < updateList.length; i++) {
    var newItem = document.createElement('li');
    newItem.innerText = updateList[i];
    updateListElem.appendChild(newItem);
  }
  updateMenu.style.display = 'block';
}

document.getElementById('closeUpdate').addEventListener('click', function () {
  document.getElementById('updateMenu').style.display = 'none';
  gamePaused = false;
});
